mapper.select:
select *
from cart
where user_id = #{userId}
  and product_id = #{productId}
limit 1

mapper.update:
update cart
set quantity      = #{quantity},
    selected_specs = #{selectedSpecs},
    total_price   = #{totalPrice},
    is_selected   = #{isSelected}
where id = #{id}

mapper.insert:
INSERT INTO cart (
    user_id, product_id, product_name, product_image,
    spec_ids, quantity, selected_specs, unit_price,
    is_selected, create_time, update_time
) VALUES (
             #{userId}, #{productId}, #{productName}, #{productImage},
             #{specIds}, #{quantity}, #{selectedSpecs}, #{unitPrice},
             #{isSelected}, #{createTime}, #{updateTime}
         )
